const days=["Воскресенье","Понедельник","Вторник","Среда","Четверг","Пятница","Суббота"],date=new Date,dayWeek=date.getDate(),day=date.getDay(),getMonthNameInGenitiveCase=(e=new Date)=>e.toLocaleString("ru",{month:"long",day:"numeric"}).split(" ")[1];function time(){const e=new Date,t=e.getHours(),n=e.getMinutes(),a=t<10?`0${t}`:t,s=n<10?`0${n}`:n,o=e.getSeconds()%2==0?"show":"hide";document.querySelectorAll(".time")[0].innerHTML=`<span class='hour'>${a}</span><span class=${o}>:</span><span>${s}</span>`,document.querySelectorAll(".time")[1].innerHTML=`<span class='hour'>${a}</span><span class=${o}>:</span><span>${s}</span>`}setInterval(time,1e3),document.querySelectorAll(".date")[0].textContent=`${days[day]} ${dayWeek} ${getMonthNameInGenitiveCase()}`,document.querySelectorAll(".date")[1].textContent=`${days[day]} ${dayWeek} ${getMonthNameInGenitiveCase()}`;